<template>	<div class="wrap-page relative">		<div class="login-wrap is-transformed gray-shadow">			<p class="login-tit">登录创客沃土</p>			<form-input :maxLength="11" placeholder="请输入登录名" type="text" v-model="account"></form-input>			<form-input type="password"						v-model="password" placeholder="请输入密码" errmsg="请输入正确的密码" class="sj-psd"></form-input>			<p class="link-forget clear">				<router-link class="forget-psd right draw-line" tag="a" to="/resetPwd">忘记密码？</router-link>			</p>			<div class="pointer login-btn ani-time">登录</div>			<div class="link-keys ">没有账号？<router-link class="ani-time" tag="a" to="/register">立即注册>></router-link>			</div>		</div>	</div></template><script type="text/ecmascript-6">	import g from './../../global';	import FormInput from "../../components/formInput.vue"	export default {		created()		{			this.init();		},		data()		{			return {				g: g,				account: '',				password: '',				errorTip: "",			}		},		watch: {			account: function (newData, oldData)			{				if (this.account != '' && this.password != '')				{					this.disabledBtn = false;				}				else				{					this.disabledBtn = true;				}			},			password: function (oldData, newData)			{				if (this.account != '' && this.password != '')				{					this.disabledBtn = false				}				else				{					this.disabledBtn = true;				}			}		},		components: {			FormInput		},		methods: {			init(){			}		}	}</script><style lang="sass" rel="stylesheet/scss" type="text/scss">	@import "../../css/base";	@import "../../css/common";	@import "../../css/login";</style>