<template>	<div class="wrap-page relative">		<div class="login-wrap is-transformed">			<p class="login-tit">创客沃土</p>			<div class="form-input relative">				<img class="absolute" :src="g.path.images+'/user-icon.png'" alt="">				<input type="text" placeholder="请输入用户名：" autocomplete="off">			</div>			<div class="form-input relative">				<img class="absolute" :src="g.path.images+'/pwd-icon.png'" alt="">				<input type="password" placeholder="请输入密码：" autocomplete="off">			</div>			<div class="pointer login-btn ani-time">登录</div>			<div class="link-keys">				<router-link class="left ani-tim relative" tag="a" to="/resetPwd">重设密码</router-link>				<router-link class="right ani-time relative" tag="a" to="/register">注册密码</router-link>			</div>		</div>	</div></template><script type="text/ecmascript-6">	import g from './../../global';	export default {		created()		{			this.init();		},		data()		{			return {				g: g,				account: '',				password: '',				errorTip: "",			}		},		watch: {			account: function (newData, oldData)			{				if (this.account != '' && this.password != '')				{					this.disabledBtn = false;				}				else				{					this.disabledBtn = true;				}			},			password: function (oldData, newData)			{				if (this.account != '' && this.password != '')				{					this.disabledBtn = false				}				else				{					this.disabledBtn = true;				}			}		},		components: {},		methods: {			init(){			}		}	}</script><style lang="sass" rel="stylesheet/scss" type="text/scss">	@import "../../css/base";	@import "../../css/common";</style><style lang="sass" rel="stylesheet/scss" type="text/scss" scoped>	@import "../../css/mixin";	@import "../../css/login";</style>